hudson.tasks.Shell.command:
cloneorpull() { if test -d $1 ; then (cd $1 && git pull) else git clone $2 fi } venv() { if ! test -d $1 ; then virtualenv $1 fi . $1/bin/activate }
createdocker() { docker-machine ls docker-machine kill default docker-machine rm -f default docker-machine create --driver virtualbox default } startdocker() { docker-machine env >/dev/null || docker-machine restart || createdocker eval $(docker-machine env default) docker-machine active } # Ensure Docker's Virtual Machine is up running first, # restart VM if it seems broken startdocker
cloneorpull common-workflow-language https://github.com/common-workflow-language/common-workflow-language.git venv cwltool-venv (. cwltool-venv/bin/activate && PIP_DOWNLOAD_CACHE=/var/lib/jenkins/pypi-cache/ pip install -U setuptools wheel pip) (. cwltool-venv/bin/activate && PIP_DOWNLOAD_CACHE=/var/lib/jenkins/pypi-cache/ python setup.py install) (. cwltool-venv/bin/activate && PIP_DOWNLOAD_CACHE=/var/lib/jenkins/pypi-cache/ pip install "cwltest>=1.0.20160825151655") (. cwltool-venv/bin/activate && cd common-workflow-language && ./run_test.sh --junit-xml=result.xml RUNNER=cwltool DRAFT=draft-2) (. cwltool-venv/bin/activate && cd common-workflow-language && ./run_test.sh --junit-xml=result.xml RUNNER=cwltool DRAFT=draft-3) (. cwltool-venv/bin/activate && cd common-workflow-language && ./run_test.sh --junit-xml=result.xml RUNNER=cwltool) (. cwltool-venv/bin/activate && cd common-workflow-language && ./run_test.sh --junit-xml=result.xml RUNNER=cwltool DRAFT=v1.1.0-dev1 EXTRA=--enable-dev) (. cwltool-venv/bin/activate && ./build-cwl-docker.sh && docker push commonworkflowlanguage/cwltool_module && docker push commonworkflowlanguage/cwltool)
